mapper.select:
SELECT COUNT(*)
FROM tbl_post
WHERE account = #{account}

select COUNT(*) from tbl_post
where genre_id = #{genreId}
and account = #{account}

select COUNT(*) from tbl_post
         where platform_id = #{platformId}
and account = #{account}

SELECT COUNT(*)
FROM tbl_post
where account= #{account}

    and post_title = #{value}

    and post_no != #{postNo}

mapper.update:
update tbl_post
set favorite=(
                select max(favorite)+1
                from tbl_post
                where account= 'admin' or #{account}
              )
WHERE post_no = #{postNo}

update tbl_post
set favorite=0
WHERE post_no = #{postNo}
